import { getByTag } from "@/api/Article";
export default {
  name: "RelatedRead",
  props: {
    tags: Array,
    size: Number
  },
  data() {
    return {
      articles: []
    };
  },
  methods: {
    getRelate() {
      let data = new FormData();
      data.append("tags", this.tags);
      data.append("page", 1);
      getByTag(data).then(res => {
        this.articles = res.data.articles;
        console.log(this.articles);
      });
    }
  },
  created() {
    this.getRelate();
  }
};
